import { Component, OnInit } from '@angular/core';
import { MovieResponse } from '../tmdb-data/Movie';
import { TmdbService } from '../tmdb.service';
import { environment } from 'src/environments/environment';
import { MenuCinemaView } from '../services/menuCinemaView.service';

@Component({
  selector: 'app-menu-cinema-view',
  templateUrl: './menu-cinema-view.component.html',
  styleUrls: ['./menu-cinema-view.component.scss']
})
export class MenuCinemaViewComponent {



  private imgRepasURL:String="data:image/jpeg;base64,";
  private imgFilmURL: String="data:image/png;base64,"
  pFilm: MovieResponse;

  constructor(private tmdb: TmdbService, private mcv:MenuCinemaView) {
    this.init();
  }

  async init() {
    this.tmdb.init( environment.tmdbKey );
    this.pFilm = await this.tmdb.getMovie(14);
  }

  get film(): MovieResponse {
    return this.pFilm;
  }

  getImgFilmURL(){
    return this.imgFilmURL;
  }

  getImgRepasURL(){
    return this.imgRepasURL;
  }

  clickFilm(){
    this.mcv.clickFilm()
  }

  clickRepas(){
    this.mcv.clickRepas();
  }
}
